% letterlayoutl - layout to print to landscape letter size paper
% set(gcf,'PaperOrientation','landscape');
% set(gcf,'PaperPosition',[0.25,0.25,10.5,8]);


set(gcf,'PaperOrientation','landscape');
set(gcf,'PaperPosition',[0.25,0.25,10.5,8]);
set(gcf, 'Units', 'inches')       
set(gcf, 'Position', [0 0.2 11 8.5])
